
DROP SCHEMA IF EXISTS `eshop`;
CREATE SCHEMA `eshop`;
use `eshop`; 

CREATE TABLE `users` (
	`id` INT NOT NULL AUTO_INCREMENT,
    `password` VARCHAR(100) NOT NULL, 
    `email` VARCHAR(50) NOT NULL, 	
    `first_name` VARCHAR(50) NOT NULL,
    `last_name` VARCHAR(50) NOT NULL,
    `phone_number` VARCHAR(100) NOT NULL,
    `birth_date` VARCHAR(100) NOT NULL,
    `username` VARCHAR(100) NOT NULL,
    PRIMARY KEY(`id`) 
)ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=latin1;

CREATE TABLE `address`(
	`id` INT NOT NULL AUTO_INCREMENT,
    `address_line_1` VARCHAR(50) NOT NULL,
    `city` VARCHAR(50) NOT NULL, 
    `country` VARCHAR(50) NOT NULL, 
    `post_code` VARCHAR(50) NOT NULL, 
    `user_id` INT NOT NULL, 
    PRIMARY KEY(`id`),  
    FOREIGN KEY(`user_id`) REFERENCES `users`(`id`) 
)ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=latin1; 

CREATE TABLE `category`(
	`id` INT NOT NULL AUTO_INCREMENT,
    `category_name` VARCHAR(100) NOT NULL,
    PRIMARY KEY(`id`)
)ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=latin1; 

CREATE TABLE `product`(
	`id` INT NOT NULL AUTO_INCREMENT, 
    `name` VARCHAR(200) NOT NULL, 
    `long_desc` VARCHAR(10000) NOT NULL, 
    `price` DECIMAL(10,2) NOT NULL,
    `category_id` INT NOT NULL,
    `brand_name` VARCHAR(100) NOT NULL, 
    `photo` BLOB DEFAULT NULL, 
    PRIMARY KEY(`id`), 
    FOREIGN KEY (`category_id`) REFERENCES `category`(`id`)
)ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=latin1; 


CREATE TABLE `inventory`(
	`id` INT NOT NULL AUTO_INCREMENT, 
    `product_id` INT NOT NULL,
    `in_stock_quantity` INT NOT NULL,
    PRIMARY KEY(`id`), 
    FOREIGN KEY (`product_id`) REFERENCES `product`(`id`)
)ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=latin1; 


CREATE TABLE `phone_details`(
	`id` INT NOT NULL AUTO_INCREMENT, 
    `product_id` INT NOT NULL, 
    `processor_brand` VARCHAR(200) NOT NULL, /* Μοντελο επεξεργαστη πχ Apple A13 Bionic */
    `processor_cores` INT NOT NULL, /*πυρηνες επεξεργαστη πχ 2+4 */ 
    `ram` VARCHAR(100) NOT NULL, /*μεγεθος ram πχ 4GB */ 
    `storage` VARCHAR(100) NOT NULL, /* Χωριτικοτητα κινητου πχ 64 GB*/ 
    `back_camera` VARCHAR(500) NOT NULL, /* Πισω καμερα πχ Ευρυγώνιος 12MP, Υπερευρυγώνιος 12MP */ 
    `front_camera` VARCHAR(100) NOT NULL, /*Selfie πχ Ευρυγώνιος 12MP */ 
    `display_size` VARCHAR(100) NOT NULL, /*μεγεθος οθονης  πχ 6,1" */ 
    `resolution` VARCHAR(100) NOT NULL, /*αναλυση οθονης πχ 1792 x 828 pixels */ 
    `operating_system` VARCHAR(300) NOT NULL, /* Λειτουργικο συστημα πχ iOS*/ 
    `battery_capacity` VARCHAR(100) NOT NULL, /*Χωρητικοτητα μπαταριας πχ 3110 mAh */
    `connection_network` VARCHAR(50) NOT NULL, /* Δικτυο συνδεσης πχ 4G ή 5G */
    PRIMARY KEY(`id`),
	FOREIGN KEY(`product_id`) REFERENCES `product` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=latin1; 

CREATE TABLE `laptop_details` (
	`id` INT NOT NULL AUTO_INCREMENT, 
    `product_id` INT NOT NULL, 
    `cpu` VARCHAR(500) NOT NULL, /*Κατασκευαστης επεξεργαστη πχ AMD*/
    `frequency` VARCHAR(100) NOT NULL,/*Συχνοτητα 3,2 GHz */
    `screen_size` VARCHAR(30) NOT NULL, /*Διασταση οθονης(διαγωνιος) πχ 15,6" */
    `screen_resolution` VARCHAR(30) NOT NULL, /*Αναλυση οθονης πχ 1920x1080 */
    `capabilities` VARCHAR(100) DEFAULT NULL,/*Δυνατοτητες οθονης πχ Full HD */
    `renewal_rate` VARCHAR(30) NOT NULL,/*Ρυθμος ανανεωσης οθονης πχ 144 Hz */
    `ram` VARCHAR(100) NOT NULL,/*Χωρητικοτητα μνημης πχ 16 GB */
	`type_of_hard_disc` VARCHAR(100) NOT NULL,/*Τυπος hard disc πχ SSD Ή HDD*/
    `hard_disc_capacity` VARCHAR(100) NOT NULL,/*Χωρητικοτητα πχ 512 GB */
    `operating_system` VARCHAR(200) NOT NULL,/*px Windows 11 Home*/
    `weight` VARCHAR(50) NOT NULL,/*Βαρος 2,1 kg */
    PRIMARY KEY(`id`),
	FOREIGN KEY(`product_id`) REFERENCES `product` (`id`)
)ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=latin1; 

CREATE TABLE `television_details` (
	`id` INT NOT NULL AUTO_INCREMENT, 
    `product_id` INT NOT NULL, 
    `screen_size` VARCHAR(30) NOT NULL, /*μεγεθος οθονης */
    `screen_resolution` VARCHAR(30) NOT NULL, /*αναλυση οθονης*/
    `renewal_rate` VARCHAR(30) NOT NULL,/*Ρυθμος ανανεωσης οθονης*/
    `sound_standards` VARCHAR(50) NOT NULL,/*Προτυπο ηχου πχ Dolby Atmos*/
    `power` VARCHAR(50) NOT NULL, /*Ισχυς ηχειου πχ 20 W */
    `supported_applications` VARCHAR(1000) DEFAULT NULL,/*Εφαρμογες πυο υποστηριζι η tv πχ Netflix, Youtube, DisneyPlus, Cosmote TV, Eon, Ant1+, Ertflix, Spotify*/
    `connector_type` VARCHAR(1000) DEFAULT NULL,/*WIFI KTLP*/
    `weight` VARCHAR(50) NOT NULL,
    PRIMARY KEY(`id`),
	FOREIGN KEY(`product_id`) REFERENCES `product` (`id`)
)ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=latin1; 

CREATE TABLE `payment`(
	`id` INT NOT NULL AUTO_INCREMENT,
    `card_number` VARCHAR(100) NOT NULL,
    `holder_name` VARCHAR(100) NOT NULL,
    `expire_date` VARCHAR(50) NOT NULL,
    PRIMARY KEY(`id`)
)ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=latin1;

CREATE TABLE `web_order`(
	`id` INT NOT NULL AUTO_INCREMENT , 
	`user_id` INT NOT NULL,
    `address_id` INT,
    `payment_id` INT NOT NULL,  
    PRIMARY KEY(`id`), 
	FOREIGN KEY(`user_id`) REFERENCES `users`(`id`), 
	FOREIGN KEY(`address_id`) REFERENCES `address`(`id`),
	FOREIGN KEY(`payment_id`) REFERENCES `payment`(`id`)
)ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=latin1; 

CREATE TABLE `web_order_quantities`(
	`id` INT NOT NULL AUTO_INCREMENT,
    `web_order_id` INT NOT NULL,
    `product_id` INT NOT NULL,
    `quantity` INT NOT NULL,
    PRIMARY KEY(`id`),
    FOREIGN KEY(`web_order_id`) REFERENCES `web_order`(`id`), 
    FOREIGN KEY(`product_id`) REFERENCES `product` (`id`)
)ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=latin1; 


DROP TABLE IF EXISTS `role`; 
CREATE TABLE `role`(
	`id` INT NOT NULL AUTO_INCREMENT, 
	`username` VARCHAR(100) NOT NULL , 
    `role_name` VARCHAR(50) NOT NULL,
 PRIMARY KEY(`id`)
)ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=latin1;

DELIMITER &
CREATE DEFINER=`root`@`localhost` PROCEDURE `InsertRandomInventory`()
BEGIN
    DECLARE i INT DEFAULT 1;
    WHILE i <= 270 DO
        SELECT `id`, FLOOR(RAND() * 101) AS `random_quantity`
        FROM `product`
        WHERE `id` = i;

        SET i = i + 1;
    END WHILE;
END &
DELIMITER ;

DELIMITER $$

CREATE TRIGGER delete_role_after_user_delete
AFTER DELETE ON users
FOR EACH ROW
BEGIN
    DELETE FROM role WHERE username = OLD.username;
END$$

DELIMITER //
CREATE TRIGGER update_role_after_user_update
AFTER UPDATE ON users
FOR EACH ROW
BEGIN
    UPDATE role SET username = NEW.username where username=OLD.username ;
END//
DELIMITER ;

